package testerer;

public class Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
int wert = 60;
		
		int i = 2;
		
		while (i <=9)
		{
			wert = wert * i;
			System.out.println(wert);
			i++;
			
		}
		i=2;
		while (i <=9)
		{
			wert = wert / i;
			System.out.println(wert);
			i++;
		}
	}

}
