package testerer;

public class Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 9;
		int b = 5;
		
		
		

		System.out.println("a=9, b=5");
		
		System.out.println(a + " + " + b + " = "+(a+b));

		System.out.println(a + " - " + b + " = "+(a-b));

		System.out.println(a + " x " + b + " = "+(a*b));
		
		System.out.println(a + " / " + b + " = "+(a/b));
		
		System.out.println(a + " % " + b + " = "+(a%b));



	}

}
